import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class LoginService {

  // constructor(private http:HttpClient) { }

  // getLoginData(){
  //   return this.http.get('https://console.firebase.google.com/project/login-60de4/database/login-60de4-default-rtdb/data')
  // }


  private apiUrl = 'https://console.firebase.google.com/project/login-60de4/database/login-60de4-default-rtdb/data/~2F';

  constructor(private http: HttpClient) { }

  fetchData(): Observable<any> {
    return this.http.get(`${this.apiUrl}/data.json`).pipe(
      map(response => {
        return response;
      })
    );
  }
}
