import { Component, OnInit } from '@angular/core';
import { LoginService } from '../service/login.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginForm: FormGroup;
  getLoginData: any;

  constructor(
    private loginService: LoginService,
    private formBuilder: FormBuilder,
    private router: Router
  ) {
    this.loginForm = this.formBuilder.group({
      email: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  ngOnInit() {
    this.fetchLoginData();
  }

  fetchLoginData() {
    this.loginService.getLoginData().subscribe((res) => {
      this.getLoginData = res;
      console.log(res);
    });
  }

  checkLogin() {
    if (this.loginForm.valid) {
      const email = this.loginForm.value.email;
      const password = this.loginForm.value.password;

      if (this.getLoginData.email === email && this.getLoginData.password === password) {
        this.router.navigateByUrl('dashboard');
      } else {
        console.log('Invalid credentials');
      }
    } else {
      console.log('Form is invalid');
    }
  }
}
<div class="container">
  <h1>Login</h1>
  <form [formGroup]="loginData">
    <div class="form-group">
      <label for="email">Email address</label>
      <input type="email" class="form-control" formControlName="email" id="email" placeholder="Enter email">
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" formControlName="password" id="password" placeholder="Password">
    </div>
    <button type="submit" class="btn btn-primary" (click)="checkLogin()">Login</button>
  </form>
</div>
