import { Component, OnInit } from '@angular/core';
import { LoginService } from '../service/login.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginForm: FormGroup;
  getLoginData: any;

  constructor(
    private loginService: LoginService,
    private formBuilder: FormBuilder,
    private router: Router
  ) {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]]
    });
  }

  ngOnInit() {
    this.fetchLoginData();
  }

  fetchLoginData() {
    this.loginService.getLoginData().subscribe((res) => {
      this.getLoginData = res;
      console.log(res);
    });
  }

  checkLogin() {
    if (this.loginForm.valid) {
      const email = this.loginForm.value.email;
      const password = this.loginForm.value.password;

      if (this.getLoginData.email === email && this.getLoginData.password === password) {
        this.router.navigateByUrl('dashboard');
      } else {
        console.log('Invalid credentials');
      }
    } else {
      console.log('Form is invalid');
    }
  }
}
<div class="container">
  <h1>Login</h1>
  <form [formGroup]="loginForm">
    <div class="form-group">
      <label for="email">Email address</label>
      <input type="email" class="form-control" formControlName="email" id="email" placeholder="Enter email">
      <div *ngIf="loginForm.get('email')?.invalid && loginForm.get('email')?.touched">
        <div *ngIf="loginForm.get('email')?.hasError('required')">
          Email is required.
        </div>
        <div *ngIf="loginForm.get('email')?.hasError('email')">
          Invalid email format.
        </div>
      </div>

    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" formControlName="password" id="password" placeholder="Password">

      <div *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched">
        <div class="text-danger" *ngIf="loginForm.get('password')?.hasError('required')">
          Password is required.
        </div>
        <div *ngIf="loginForm.get('password')?.hasError('minlength')">
          Password should have a minimum of 8 characters.
        </div>
      </div>
    </div>
    <button type="submit" class="btn btn-primary" [disabled]="loginForm.invalid" (click)="checkLogin()">Login</button>
  </form>
</div>
